import {View, StyleSheet } from 'react-native'
import ThemedText from './ThemedText';

const style = StyleSheet.create({
	panel: {
		display: 'flex',
		flexDirection: 'row'
	},
	pair: {
		flexGrow: 1
	}
})

const InfoPair = ({ top, bottom }) => (
	<View>
		<ThemedText fontWeight='bold'>
			{top}
		</ThemedText>
		<ThemedText>
			{bottom}
		</ThemedText>
	</View>
)

const PanelBottom = ( { stars, forks, reviews, rating }) => (
	<View style={style.panel}>
		<InfoPair top={stars} bottom='Stars'/>
		<InfoPair top={forks} bottom='Forks'/>
		<InfoPair top={reviews} bottom='Reviews'/>
		<InfoPair top={rating} bottom='Rating'/>
	</View>
) 

export default PanelBottom
