import Constants from 'expo-constants'
import { Platform } from 'react-native';

const theme = {
	colors: {
		textPrimary: '#24292e',
		textSecondary: '#586069',
		primary: '#0366d6',
		navBar: '#25292e',
		navBarTabText: 'white',
		separator: 'lightgrey'
	},
	fontSizes: {
		body: 14,
		subheading: 16,
	},
	fonts: {
		main: Platform.select({
			ios: 'Arial',
			android: 'Roboto'
		})()
	},
	fontWeights: {
		normal: '400',
		bold: '700',
	},
	spacing: {
		navBar: Constants.statusBarHeight,
		imageHeight: 50,
		imageWidth: 50,
		separator: 10,
		headerPadding: 5
	}
};

export default theme;
