import React from 'react'
import { FlatList, View, StyleSheet, Linking } from 'react-native'
import RepositoryItem from './RepositoryItem';
import theme from '../../theme';

const styles = StyleSheet.create({
	separator: {
		height: theme.spacing.separator,
		backgroundColor: theme.colors.separator 
	}
})

const ItemSeparator = () => <View style={styles.separator}/>

const RepositoryListContainer = ({ repos }) => {
	const repoNodes = repos
		? repos.repositories.edges.map(e => e.node)
		: [];

const handleOpenGithub = url => {
	Linking.openURL(url)
}

	return (
		<FlatList
			data={repoNodes}
			renderItem={<RepositoryItem handleOpenGithub={handleOpenGithub}/>}
			keyExtractor={(_, idx) => idx.toString()}
			ItemSeparatorComponent={ItemSeparator}
		/>
	)
}

export default RepositoryListContainer
