import { View } from 'react-native'
import theme from '../../theme'
import ThemedText from '../ThemedText'

const ReviewItem = ({ review }) => {
	return (
		<View 
			style={{
				height: 40
			}}
		>
			<View
				style={{
					width: 40,
					height: 40,
					borderRadius: 20,
					borderWidth: 3,
					borderColor: theme.colors.primary
				}}
			>
				<ThemedText
					color='primary'
					fontWeight='bold'
					fontSize='subheading'
					style={{
						alignSelf: 'center'
					}}
				>
					{review.rating}
				</ThemedText>
			</View>
		</View>
	)
}

export default ReviewItem
