import {View, Image, StyleSheet } from 'react-native'
import ThemedText from './ThemedText';
import theme from '../theme';

const style = StyleSheet.create({
	panelTop: {
		display: 'flex',
		flexDirection: 'row'
	},
	avatarImage:{
		height: theme.spacing.imageHeight,
		width: theme.spacing.imageWidth,
		borderRadius: 10
	},
	nub: {
		backgroundColor: theme.colors.primary,
		flexGrow: 0,
		borderRadius: 5,
		color: 'white',
		padding: 5,
		overflow: 'hidden',
		alignSelf: 'flex-start',
		marginTop: 8
	},
	header:{
		marginLeft: 20
	},

})

const PanelTop = ({ imageUrl, title, description, language }) => (
	<View style={style.panelTop}>
		<Image
			style={style.avatarImage}
			source={{ uri: imageUrl }}
		/>
		<View style={style.header}>
			<ThemedText
				fontWeight='bold'
				fontSize={'subheading'}
				withPadding
			>
				{title}
			</ThemedText>
			<ThemedText
				color='textSecondary'
			>
				{description}
			</ThemedText>
			<View style={style.nub}>
				<ThemedText fontWeight='bold' color='textSecondary'>
					{language}
				</ThemedText>
			</View>
		</View>
	</View>

)

export default PanelTop;
